import { readFileSync } from 'fs';
import { fileSync } from 'tmp';

import { generate } from '.';

const expectedPetStoreFile = `/* eslint-disable */
/* autogenerated by openapi-superstruct */

import * as s from 'superstruct';

const struct_Pet = s.object({
  "id": s.integer(),
  "name": s.string(),
  "tag": s.optional(s.string()),
});
const struct_Pets = s.array(s.lazy(() => struct_Pet));
const struct_Error = s.object({
  "code": s.integer(),
  "message": s.string(),
});

export const structs = {
  "Pet": struct_Pet,
  "Pets": struct_Pets,
  "Error": struct_Error,
};

export type Pet = s.Infer<typeof structs['Pet']>;
export type Pets = s.Infer<typeof structs['Pets']>;
export type Error = s.Infer<typeof structs['Error']>;

interface PreparedGet {
  method: 'get';
  url: string;
}
interface PreparedPut<Body> {
  method: 'put';
  url: string;
  body: Body;
}
interface PreparedPost<Body> {
  method: 'post';
  url: string;
  body: Body;
}
interface PreparedDelete {
  method: 'delete';
  url: string;
}
interface PreparedPatch<Body> {
  method: 'patch';
  url: string;
  body: Body;
}

const path_listPets = (limit: number): PreparedGet => {
  const method = 'get';
  const url = '/pets';
  return { method, url };
}
const path_createPets = (): PreparedPost<unknown> => {
  const method = 'post';
  const url = '/pets';
  return { method, url };
}
const path_showPetById = (petId: string): PreparedGet => {
  const method = 'get';
  const url = '/pets/{petId}';
  return { method, url };
}
`;

describe('generate', () => {
  describe('object input', () => {
    const input = {
      components: {
        schemas: {
          Model: {
            type: 'string'
          }
        }
      }
    };
    const expectedOutput = `/* eslint-disable */
/* autogenerated by openapi-superstruct */

import * as s from 'superstruct';

const struct_Model = s.string();

export const structs = {
  "Model": struct_Model,
};

export type Model = s.Infer<typeof structs['Model']>;
`;
    it('is allowed in generate', async () => {
      const file = await generate({ input });
      expect(file).toEqual(expectedOutput);
    });
    it('output parameter', async () => {
      const { name: path } = fileSync();
      await generate({ input, output: path });
      const outputFile = readFileSync(path).toString();
      expect(outputFile).toEqual(expectedOutput);
    });
  });
  describe('http input', () => {
    it('works for json', async () => {
      const input =
        'http://raw.githubusercontent.com/OAI/OpenAPI-Specification/main/examples/v3.0/petstore.json';
      const file = await generate({ input });
      expect(file).toEqual(expectedPetStoreFile);
    });
    it('works for yaml', async () => {
      const input =
        'http://raw.githubusercontent.com/OAI/OpenAPI-Specification/main/examples/v3.0/petstore.yaml';
      const file = await generate({ input });
      expect(file).toEqual(expectedPetStoreFile);
    });
  });
  describe('https input', () => {
    it('works for json', async () => {
      const input =
        'https://raw.githubusercontent.com/OAI/OpenAPI-Specification/main/examples/v3.0/petstore.json';
      const file = await generate({ input });
      expect(file).toEqual(expectedPetStoreFile);
    });
    it('works for yaml', async () => {
      const input =
        'https://raw.githubusercontent.com/OAI/OpenAPI-Specification/main/examples/v3.0/petstore.yaml';
      const file = await generate({ input });
      expect(file).toEqual(expectedPetStoreFile);
    });
  });
});
